import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IRent } from '../rents/rent';
import { RentService } from '../rents/rents.service';

@Component({
  selector: 'app-rent-details',
  templateUrl: './rentdetails.component.html',
  styleUrls: ['./rentdetails.component.css']
})
export class RentDetailsComponent implements OnInit {
  submittedRent: IRent = {};  // Initialize with an empty object or default values

  constructor(private route: ActivatedRoute, private rentService: RentService) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const rentId = params.get('rentId');

      if (rentId) {
        this.rentService.getRentById(+rentId).subscribe(rent => {
          // Check if rent is not null before assignment
          if (rent) {
            this.submittedRent = rent;
          }
        });
      }
    });
  }
}
<div class="container">
  <h2>Rent Details</h2>
  <div>
    <p><strong>Start Date:</strong> {{ submittedRent.startDate | date }}</p>
    <p><strong>End Date:</strong> {{ submittedRent.endDate | date }}</p>
    <p><strong>Calculated Price:</strong> {{ submittedRent.price | convertToCurrency:'NOK' }}</p>
    <!-- Add more details as needed -->
  </div>
</div>
