// listingdetail.component.ts

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ListingService } from '../services/listing.service'; // Import your listing service or wherever deleteListing is implemented

@Component({
  selector: 'app-listing-detail',
  templateUrl: './listingdetail.component.html',
  styleUrls: ['./listingdetail.component.css'],
})
export class ListingDetailComponent implements OnInit {
  listing: Listing; // Assuming you have a Listing class

  constructor(
    private route: ActivatedRoute,
    private listingService: ListingService // Adjust this to your actual service
  ) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe((params) => {
      const listingId = params.get('id');
      this.listing = this.listingService.getListingById(listingId);
    });
  }

  deleteListing(listing: Listing): string {
    // Assuming you have a route called '/delete' and you want to pass the listing ID as a parameter
    return `/delete/${listing.id}`;
  }
}
