import { Component } from '@angular/core';
import { AuthService } from './auth.service'; // Import the AuthService


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  user = {
    email: '',
    password: ''
  };
  constructor(private authService: AuthService) { } // AuthService injected here


  onLogin() {
    this.authService.login(this.user.email, this.user.password).subscribe({
      next: (response) => {
        console.log('Login successful:', response);
        // Here you would typically store the token and redirect the user
      },
      error: (error) => {
        console.error('Login failed:', error);
      }
    });
  }
}

<form (ngSubmit)="onLogin()">
  <div>
    <label for="email">Email:</label>
    <input type="email" id="email" [(ngModel)]="user.email" name="email" required>
  </div>
  <div>
    <label for="password">Password:</label>
    <input type="password" id="password" [(ngModel)]="user.password" name="password" required>
  </div>
  <button type="submit">Login</button>
</form>
