import { Component } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ListingService } from './listings.service';

@Component({
  selector: "app-listings-listingform",
  templateUrl: "./listingform.component.html"
})
export class ListingformComponent {
  listingForm: FormGroup;
  isEditMode: boolean = false;
  listingId: number = -1;

  constructor(
    private _formbuilder: FormBuilder,
    private _router: Router,
    private _route: ActivatedRoute,
    private _listingService: ListingService
  ) {
    this.listingForm = _formbuilder.group({
      name: ['', Validators.required],
      price: [0, Validators.required],
      description: [''],
      imageUrl: [''],
      AdditionalImageUrl: [''],
      Rooms: [0, Validators.required], 
      Toilets: [0, Validators.required],
      Area: [0, Validators.required], 
      Address: [''] 
   
    });
  }

  onSubmit() {
    console.log("ListingCreate form submitted:");
    console.log(this.listingForm);
    const newListing = this.listingForm.value;
    if (this.isEditMode) {
      this._listingService.updateListing(this.listingId, newListing)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/listings']);
          }
          else {
            console.log('Listing update failed');
          }
        });
    }
    else {
      this._listingService.createListing(newListing)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/listings']);
          }
          else {
            console.log('Listing creation failed');
          }
        });
    }
  }

  backToListings() {
    this._router.navigate(['/listings']);
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      if (params['mode'] === 'create') {
        this.isEditMode = false; // Create mode
      } else if (params['mode'] === 'edit') {
        this.isEditMode = true; // Edit mode
        this.listingId = +params['id']; // Convert to number
        this.loadListingForEdit(this.listingId);
      }
    });
  }

  loadListingForEdit(listingId: number) {
    this._listingService.getListingById(listingId)
      .subscribe(
        (listing: any) => {
          console.log('retrived listing: ', listing);
          this.listingForm.patchValue({
            name: listing.Name,
            price: listing.Price,
            description: listing.Description,
            imageUrl: listing.ImageUrl,
            AdditionalImageUrl: listing.AdditionalImageUrl,
            Rooms: listing.Rooms || 0, 
            Toilets: listing.Toilets || 0, 
            Area: listing.Area || 0, 
            Address: listing.Address || ''
          });
        },
        (error: any) => {
          console.error('Error loading listing for edit:', error);
        }
      );
  }

}
<h2>{{ isEditMode ? 'Update' : 'Create' }} Listing</h2>

<form [formGroup]="listingForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="name">Name:</label>
    <input type="text" id="name" formControlName="name" class="form-control" />
  </div>
  <div class="form-group">
    <label for="price">Price:</label>
    <input type="number" id="price" formControlName="price" class="form-control" />
  </div>
  <div class="form-group">
    <label for="description">Description:</label>
    <textarea id="description" formControlName="description" class="form-control"></textarea>
  </div>
  <div class="form-group">
    <label for="imageUrl">Image URL:</label>
    <input type="text" id="imageUrl" formControlName="imageUrl" class="form-control" />
  </div>
  <div class="form-group">
    <label for="AdditionalImageUrl">Image URL:</label>
    <input type="text" id="AdditionalImageUrl" formControlName="AdditionalImageUrl" class="form-control" />
  </div>
  <div class="form-group">
    <label for="Rooms">Rooms:</label>
    <input type="text" id="Rooms" formControlName="Rooms" class="form-control" />
  </div>
  <div class="form-group">
    <label for="Toilets">Toilets:</label>
    <input type="text" id="Toilets" formControlName="Toilets" class="form-control" />
  </div>
  <div class="form-group">
    <label for="Area">Area:</label>
    <input type="text" id="Area" formControlName="Area" class="form-control" />
  </div>
  <div class="form-group">
    <label for="Address">Address:</label>
    <input type="text" id="Address" formControlName="Address" class="form-control" />
  </div>
  <button class='btn btn-primary' type="submit" [disabled]="listingForm.invalid">
    {{ isEditMode ? 'Update' : 'Create' }} Listing
  </button>
  <button class='btn btn-secondary' (click)="backToListings()">Cancel</button>
</form>
