import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { RentService } from '../path-to-rent-service/rents.service'; // Update the path

@Component({
  selector: 'app-rentdetails',
  templateUrl: './rentdetails.component.html',
  styleUrls: ['./rentdetails.component.css']
})
export class RentDetailsComponent implements OnInit {
  rentDetails: any; // Replace 'any' with the actual type of your rent details

  constructor(
    private route: ActivatedRoute,
    private rentService: RentService // Inject RentService
  ) { }

  ngOnInit(): void {
    // Retrieve rentId from the route parameters
    const rentId = +this.route.snapshot.paramMap.get('id');

    // Call the method to fetch rent details
    this.fetchRentDetails(rentId);
  }

  private fetchRentDetails(rentId: number): void {
    // Use RentService to get rent details by ID
    this.rentService.getRentById(rentId).subscribe(
      (rentDetails) => {
        this.rentDetails = rentDetails;
      },
      (error) => {
        console.error('Error fetching rent details:', error);
      }
    );
  }
}
